import React from 'react';
import './style.css';

function Hero({ title, description, link, repo, react, desktopImg, mobileImg }) {
    const id = title.replace(' ', '-').toLowerCase();

    return (
        <div id={id} className='sticky-container'>
            <div className='project-preview-hero'>
                <div id='hero-container'>
                    <div className='preview-thumbnail-hero' style={{
                        transform: `matrix(${heroSize}, 0, 0, ${heroSize}, ${heroPosition}, 0)`
                    }}
                    >
                        <a
                            href={link}
                            target='_blank'
                            rel='noreferrer'
                            className='project-thumbnail-desktop-link'
                            style={{
                                transform: `translateY(-${Math.floor(foundArkMod / (5 * parallaxSpeed))}px)`
                            }}
                        >
                            <img src={desktopImg} alt='found ark desktop preview' className='project-thumbnail-desktop'></img>
                        </a>

                        <a
                            href={link}
                            target='_blank'
                            rel='noreferrer'
                            className='project-thumbnail-phone-link'
                            style={{
                                transform: `translateY(${Math.floor(foundArkMod / (10 * parallaxSpeed))}px)`,
                                width: `20%`
                            }}
                        >
                            <img src={mobileImg} alt='found ark phone preview' className='project-thumbnail-phone'></img>
                        </a>
                    </div>
                </div>

                <div
                    className={
                        foundArkPos <= -spacerLarge + pageOffset * 1.5 || window.innerWidth <= 1024
                            ? 'preview-details-right show-description'
                            : 'preview-details-right hide-description'
                    }
                >
                    <h2 className='preview-title'>Found Ark</h2>

                    <p
                        className={
                            foundArkPos <= -spacerLarge + pageOffset * 1.25 || window.innerWidth <= 1024
                                ? 'project-description-right show-description'
                                : 'project-description-right hide-description'
                        }
                    >
                        {description}
                    </p>

                    <div
                        className={
                            foundArkPos <= -spacerLarge + pageOffset || window.innerWidth <= 1024 ? 'preview-links show-description' : 'preview-links hide-description'
                        }
                    >
                        <a href={link} target='_blank' rel='noreferrer' className='icon-link-hero'>
                            <img src='./assets/icons/web-icon.png' alt='deployed application link' className='icon'></img>
                            <p className='icon-description-right'>Deployed Application</p>
                        </a>
                        <a href={repo} target='_blank' rel='noreferrer' className='icon-link-hero'>
                            <img src='./assets/icons/GitHub-Mark-Light-120px-plus.png' alt='GitHub repository link' className='icon'></img>
                            <p className='icon-description-right'>GitHub Repository</p>
                        </a>
                        <div className='icon-link-hero'>
                            <img src='./assets/icons/React-icon.png' alt='GitHub repository link' className='icon'></img>
                            <p className='icon-description-right'>Made with React</p>
                        </div>
                    </div>
                </div>
            </div>

            {window.innerWidth > 1024 && <div className='sticky-spacer-large' style={{ height: `${spacerLarge + pageOffset / 2}px` }}></div>}
        </div>
    );
}

export default Hero;
