import React, { useState, useEffect } from 'react';
import './style.css';

function Nav({ currentPage, setCurrentPage }) {

    const [navStatus, setNavStatus] = useState('hidden');

    const navDetail = '/';

    const handleMobileNav = () => {

        if (window.innerWidth > 768) {
            setNavStatus('');
        } else {
            setNavStatus('hidden');
        };

    };

    const handleMobileNavClick = () => {

        if (window.innerWidth > 768) {
            return;
        } else if (navStatus === 'hidden' || 'nav-closed') {
            setNavStatus('nav-open');
        } else if (navStatus === 'nav-open') {
            setNavStatus('nav-closed');
        };

    };

    useEffect(() => {
        window.addEventListener("resize", handleMobileNav);
        handleMobileNav();
        // eslint-disable-next-line
    }, []);

    return (

        <>

            <div onClick={handleMobileNavClick} className="mobile-menu-button">
                <p>{navDetail}</p>
                <p>{navDetail}</p>
            </div>

            <ul className={"nav " + navStatus}>

                <li className="nav-item nav-detail">
                    <p>{navDetail}</p>
                    <p>{navDetail}</p>
                </li>

                <li className="nav-item">
                    <a href="#about" onClick={() => setCurrentPage('About')} className={currentPage === 'About' ? 'nav-link active' : 'nav-link'}>
                        About
                    </a>
                </li>

                <li className="nav-item">
                    <a href="#work" onClick={() => setCurrentPage('Work')} className={currentPage === 'Work' ? 'nav-link active' : 'nav-link'}>
                        Work
                    </a>
                </li>

                <li className="nav-item">
                    <a href="#contact" onClick={() => setCurrentPage('Contact')} className={currentPage === 'Contact' ? 'nav-link active' : 'nav-link'}>
                        Contact
                    </a>
                </li>

                <li className="nav-item">
                    <a href="/assets/resume/eli-wood-resume.pdf" target="_blank" rel="noreferrer" className="nav-link">
                        Resume
                    </a>
                </li>

            </ul>

        </>

    );

};

export default Nav;
