import React, { useState, useEffect } from 'react';
import './style.css';

function Work({ currentPage, setCurrentPage }) {

    // project positions
    const [foundArkPos, setFoundArkPos] = useState();
    const [recipePos, setRecipePos] = useState();
    const [weatherPos, setWeatherPos] = useState();
    const [blogPos, setBlogePos] = useState();
    const [socialPos, setSocialPos] = useState();
    const [commercePos, setCommercePos] = useState();

    // project modifiers
    const [foundArkMod, setFoundArkMod] = useState(0);
    const [recipeMod, setRecipeMod] = useState(0);
    const [weatherMod, setWeatherMod] = useState(0);
    const [blogMod, setBlogeMod] = useState(0);
    const [socialMod, setSocialMod] = useState(0);
    const [commerceMod, setCommerceMod] = useState(0);

    // parallax scrolling
    const handleScroll = () => {

        // get window height
        const windowHeight = window.innerHeight;

        // get elements from the page
        setFoundArkPos(document.getElementById('found-ark').getBoundingClientRect().top);
        setRecipePos(document.getElementById('recipe').getBoundingClientRect().top);
        setWeatherPos(document.getElementById('weather').getBoundingClientRect().top);
        setBlogePos(document.getElementById('blog').getBoundingClientRect().top);
        setSocialPos(document.getElementById('social').getBoundingClientRect().top);
        setCommercePos(document.getElementById('commerce').getBoundingClientRect().top);

        // arrays of all elements and state setters
        const allPositions = [foundArkPos, recipePos, weatherPos, blogPos, socialPos, commercePos];
        const setAllMods = [setRecipeMod, setWeatherMod, setBlogeMod, setFoundArkMod, setSocialMod, setCommerceMod];

        // calculate modifier and set to state
        for (let i = 0; i < allPositions.length; i++) {

            if (allPositions[i] < 0) {
                setAllMods[i](Math.floor(Math.sqrt((windowHeight) * 40)));
            } else if (allPositions[i] < windowHeight) {
                setAllMods[i](Math.floor(Math.sqrt((windowHeight - allPositions[i]) * 40)));
            };

        };

    };

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        handleScroll();
    }, []);

    return (

        <div className="page">

            {/* banner */}
            <div className="intro">

                <h2>Hi, my name is Eli and I'm a software developer.</h2>

                <h3>I'm always open to new opportunities. Check out my portfolio</h3>
                <h3>and let me know if you're interested in working together.</h3>

                <a href="#contact" onClick={() => setCurrentPage('Contact')} className="contact-button">
                    <button>Contact</button>
                </a>

            </div>

            {/* found ark */}
            <div id="found-ark" className="project-preview-left">

                <div className="preview-thumbnail-left">

                    <a href="https://found-ark.herokuapp.com" target="_blank" rel="noreferrer" className="project-thumbnail-desktop-link" style={{ transform: `translateY(-${foundArkMod}px)` }}>
                        <img src="assets/work/found-ark/desktop-mockup.png" alt="found ark desktop preview" className="project-thumbnail-desktop"></img>
